mapper.select:
select
  id, num, sale_price, sale_order_id, model_id, sale_cart_id
from sale_cart_item


        and id = #{saleCartItem.id}


        and num = #{saleCartItem.num}


        and sale_price = #{saleCartItem.salePrice}


        and sale_order_id = #{saleCartItem.saleOrderId}


        and model_id = #{saleCartItem.modelId}


        and sale_cart_id = #{saleCartItem.saleCartId}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from sale_cart_item


        and id = #{id}


        and num = #{num}


        and sale_price = #{salePrice}


        and sale_order_id = #{saleOrderId}


        and model_id = #{modelId}


        and sale_cart_id = #{saleCartId}
